INSERT INTO wine_taste.category (category_id, category_name, category_description, category_image) VALUES (1, 'Red Wine', 'Red wine is a type of wine made from dark-colored grape varieties. The actual color of the wine can range from intense violet, typical of young wines, through to brick red for mature wines and brown for older red wines.', null);
INSERT INTO wine_taste.category (category_id, category_name, category_description, category_image) VALUES (2, 'White Wine', 'White wine is a wine that is fermented without skin contact. The colour can be straw-yellow, yellow-green, or yellow-gold. It is produced by the alcoholic fermentation of the non-coloured pulp of grapes, which may have a skin of any colour. ', null);